import "../assets/css/galeria.css";
import useImages from "../hooks/useImages";
import Heart from "./Heart";

export default function Home() {


  const { imagenes, setFavorito, favoritos } = useImages();

  console.log("desde galeria", imagenes);


  const guardarFoto = (e) => {
    setFavorito(e.target.currentSrc)   
    setFavoritos(...favoritos ,...favorito)
  }

  return (
    <>
      <div className="galeria grid-columns-5 p-3">
        {imagenes.map((imagen) => (
          
          <img onClick={guardarFoto} key={imagen.id} src={imagen.src.tiny}></img>
        ))}


      </div>
    </>
  );
}
