  console.log("FAVORITOS desde galeria", imagenes);

  return (
    <>
      <div className="galeria grid-columns-5 p-3">
        {imagenes.map((imagen) => (
          <div
            className="foto"
            onClick={() => setFavorito(imagen)}
            key={imagen.id}
