    setPublicaciones([...publicaciones]);
  };

  return (
    <ImagenesContext.Provider
      value={{
        imagenes,
        setImagenes,
        favoritos,
        favorito,
        setFavorito,
      }}
    >
      {children}
    </ImagenesContext.Provider>
  );
}

