import { useState, createContext } from "react";

const ImagenesContext = createContext();

function ImagenesProvider({ children }) {
  const [imagenes, setImagenes] = useState([]);


  const [favoritos, setFavoritos] = useState({
    ciudad: '',
    pais: ''
})

const arregloFotos = e => {
  setFavoritos({...favoritos, [e.target.name]: e.target.value});
}

  return (
    <ImagenesContext.Provider
      value={{
        imagenes,
        setImagenes,
        favoritos,
        setFavoritos,
        arregloFotos
      }}>
      {children}
    </ImagenesContext.Provider>
  );
}

export { ImagenesProvider };

export default ImagenesContext;
