import { useState, createContext, useEffect } from "react";

const ImagenesContext = createContext();

function ImagenesProvider({ children }) {
  const [imagenes, setImagenes] = useState([]);
  const [favorito, setFavorito] = useState("");
  const [favoritos, setFavoritos] = useState([]);


  console.log("favorito desde context", favorito);

  useEffect(() => {
    if (favorito != "") {
      setFavoritos([...favoritos, favorito]);

      for(let favorito of favoritos ){
        console.log('favorito ', favorito)
      }
    }



  }, [favorito]);

  return (
    <ImagenesContext.Provider
      value={{
        imagenes,
        setImagenes,
        favoritos,
        favorito,
        setFavorito,

      }}
    >
      {children}
    </ImagenesContext.Provider>
  );
}

export { ImagenesProvider };

export default ImagenesContext;
